SELECT "elife-data-warehouse-prod.ods.ride_dispatch_1".id AS ride_id, "elife-data-warehouse-prod.ods.ride_dispatch_1".to_fleet_id AS dispatch_fleet_id, "elife-data-warehouse-prod.ods.ride_auction_ride_1".auction_id AS auction_id, "elife-data-warehouse-prod.ods.ride_auction_fleet_1".fleet_id AS auction_fleet_id, CASE WHEN ("elife-data-warehouse-prod.ods.ride_dispatch_1".to_fleet_id = "elife-data-warehouse-prod.ods.ride_auction_fleet_1".fleet_id) THEN 'auction' ELSE 'dispatch' END AS dispatch_type
FROM "elife-data-warehouse-prod.ods.ride_dispatch" AS "elife-data-warehouse-prod.ods.ride_dispatch_1" LEFT OUTER JOIN "elife-data-warehouse-prod.ods.ride_auction_ride" AS "elife-data-warehouse-prod.ods.ride_auction_ride_1" ON "elife-data-warehouse-prod.ods.ride_dispatch_1".ride_id = "elife-data-warehouse-prod.ods.ride_auction_ride_1".ride_id LEFT OUTER JOIN "elife-data-warehouse-prod.ods.ride_auction_fleet" AS "elife-data-warehouse-prod.ods.ride_auction_fleet_1" ON "elife-data-warehouse-prod.ods.ride_auction_ride_1".auction_id = "elife-data-warehouse-prod.ods.ride_auction_fleet_1".auction_id
 LIMIT 100